import React from 'react'
import { useState } from 'react'
import { Button, Card, CardActions, CardContent, Typography, Grid, Dialog, DialogContent, DialogTitle, DialogActions, DialogContentText } from '@material-ui/core';
import vs from '../images/vs.png'
import { getMatchDetail } from '../api/Api';

const MyCard = ({ match }) => {

    const [detail, setDetail] = useState({})

    const [open, setOpen] = useState(false)

    const handleClick = (id) => {
        getMatchDetail(id)
            .then((data) => {
                console.log("Match Data", data)
                setDetail(data)
                handleOpen()
            })
            .catch((error) => console.log(error))
    }

    const handleClose = () => {
        setOpen(false)
    }

    const handleOpen = () => {
        setOpen(true)
    }

    const getDailog = () => {

    }

    return (
        <>
            <Card style={{ marginTop: 20 }}>
                <CardContent>
                    <Grid container justify="center" alignItems="center" spacing={6}>
                        <Grid item>
                            <Typography>{match["team-1"]}</Typography>
                        </Grid>
                        <Grid item>
                            <img style={{ width: 50 }} src={vs} />
                        </Grid>
                        <Grid item>
                            <Typography>{match["team-2"]}</Typography>
                        </Grid>
                    </Grid>
                </CardContent>
                <CardActions>
                    <Grid container justify="center">
                        <Button
                            onClick={() => handleClick(match.unique_id)}
                            item
                            variant="outlined"
                            color="primary">
                            Show Detail
                </Button>
                        <Button style={{ marginLeft: 10 }} item variant="outlined" color="primary">
                            Start Time {new Date(match.dateTimeGMT).toLocaleString()}
                        </Button>
                    </Grid>
                </CardActions>
            </Card>
            <getDailog />
        </>
    )
}
export default MyCard
